import React,{Component} from 'react';

class Counter extends Component{
    state={
        count:0,
        styles:{
            fontSize:50,
            fontWeight:'bold'
        },
        tags:['tag1','tag2','tag3'],
    };

    getTag(){
        if (this.state.tags.length===0) return <p></p> 
    }

    render(){
        return (<React.Fragment> 
                    <span style={this.state.styles}  className={this.state.count !==0?'badge badge-primary m-2':'badge badge-warning m-2'}>{getcount(this.state)}</span>
                    <button style={this.state.styles}  className='btn btn-secondary btn-lg'>increamnet</button>
                    <ol>
                        {this.state.tags.map((tag)=> <li key={tag}>tag</li>)}
                    </ol>
               </React.Fragment>);
    }

}
export default Counter;

function getcount(state){
    const {count}=state
    return count ===0? "Zero" : count ; 
}
