import Counter from './counter'

class Counters extends Component {

    state={
        counters:[
            {id:0,value:0},
            {id:1,value:1},
            {id:2,value:2},
            {id:3,value:3}
        ]
    };

    deleteHandle(id){
       const counters=this.state.counters.filter(counter => counter.id !==id)
        this.setState({counters:counters})
    }
    change = (counter)=> {
        this.setState( { count :this.state.count +1 }); 



     }

    render(){
        return <div>
            <button className='btn btn-primary btn-sm m-2' onClick={this.resetHandler}>Reset</button>
            {this.state.counters.map(counter => <Counter onDelete={()=>{this.deleteHandle(counter.id)}} key={counter.id} counter={counter}/>)}
        </div>
    }
}
