import React,{Component} from 'react';

class Counter extends Component{
    state={
        count:0,
        styles:{
            fontSize:50,
        }
    }
    render(){
        return <React.Fragment>
                    <h1 style={this.state.styles} className='badge badge-prime m-2'>{getcount(this.state)}</h1>
                    <button className='btn btn-secondary btn-lg'>increamnet</button>
               </React.Fragment>;
    }

}
export default Counter;

function getcount(state){
    const {count}=state

    const data=count ===0? "Zero" : count 
    return data ; 
}
