    </div>
    <div class="col-sm">
      One of three columns
    </div>
  </div>
</div>
                    <button  onClick={()=> {this.props.onIncrement(this.props.counter)}}  className='btn btn-secondary btn-lg'>
                        +
                    </button>
                    {this.getdecreament()}

                    <button className="btn btn-danger btn m-5" onClick={()=>{this.props.onDelete(this.props.counter.id)}}>
                        delete
                    </button>
                </div>
               );
    }

}
