import React, {Component} from 'react'
import Counter from './counter'

class Counters extends Component {

    state={
        counters:[
            {id:0,value:0},
            {id:1,value:1},
            {id:2,value:2},
            {id:3,value:3}
        ]
    };

    deleteHandle(id){
       const counters=this.state.counters.filter(counter => counter.id !==id)
        this.setState({counters:counters})
    };
    change = (counter)=> {
        const counters=this.state.counters
        let index=counters.indexOf(counter);
        counters[index].value++ ;

        this.setState( { counters }); 
    };

    resetHandler(){
        this.setState({counters:this.state.counters.map(count =>
            count )});
    };


    render(){
        return(
        <div>
            <div><button className='btn btn-primary btn-sm m-2' onClick={this.resetHandler}>Reset</button></div> 
            {this.state.counters.map(counter => <Counter onIncrement onDelete={()=>{this.deleteHandle(counter.id)}} key={counter.id} value={counter.value} counter={counter}/>)}
        </div>)
    }
}
export default Counters;
