import React,{Component} from 'react';

class Counter extends Component{
    state={
        count:0,
        styles:{
            fontSize:50,
            fontWeight:'bold'
        },
        tags:['tag1','tag2','tag3'],
    }
    render(){
        return (<React.Fragment> 
                    <span style={this.state.styles}  className={this.state.count !==0?'badge badge-primary m-2':'badge badge-warning m-2'}>{getcount(this.state)}</span>
                    <button style={this.state.styles}  className='btn btn-secondary btn-lg'>increamnet</button>

               </React.Fragment>);
    }

}
export default Counter;

function getcount(state){
    const {count}=state

    const data=count ===0? "Zero" : count 
    return data ; 
}
