    // }

    getdecreament=()=>{
        const disbled= <button disabled onClick={()=> {this.props.onDecrement(this.props.counter)}}  className='btn btn-secondary btn-lg '>
                        -
                       </button>
        const enabled= <button  onClick={()=> {this.props.onDecrement(this.props.counter)}}  className='btn btn-secondary btn-lg '>
                        -
                        </button>
    return this.props.counter.value !==0 ? enabled :disbled

