    render(){


        return (
        <React.Fragment> 

            <div className="container">
                <div className="row row-cols-auto">
                    <div className="col">

                    </div>
                    <div className="col">

                    </div>
                    <div className="col">

                    </div>
                </div>
            </div>

            <div class="container">
                <div class="row row-cols-auto">
                    <div class="col"><span className={this.props.value!==0?'badge badge-primary m-2':'badge badge-warning m-2'}>
                    {this.props.value}
                    </span>
                </div>
                <div class="col">
                         <button  onClick={()=> {this.props.onIncrement(this.props.counter)}}  className='btn btn-secondary btn-lg'>
                            +
                        </button>
                </div>
                <div class="col">
                    <button className="btn btn-danger btn m-5" onClick={()=>{this.props.onDelete(this.props.counter.id)}}>
                        delete
                    </button>
                </div>

                </div>
            </div>        


        </React.Fragment>);
    }

}
