import React, {Component} from 'react'
import Counter from './counter'

class Counters extends Component {
    render(){
        return(
        <div>
            <div><button className='btn btn-primary btn-sm m-2' onClick={this.resetHandler}>Reset</button></div> 
            {this.p.counters.map(counter => <Counter onIncrement={this.props.onIncrement} onDelete={()=>{this.props.onDelete(counter.id)}} key={counter.id} value={counter.value} counter={counter}/>)}
        </div>)
    }
}
export default Counters;
