import React,{Component} from 'react';

class Counter extends Component{
    // state={
    //     count:this.props.counter.value,
    //     styles:{
    //         fontSize:50,
    //         fontWeight:'bold'
    //     },
    //     tags:[],
    // };

    // getTag(){
    //     if (this.state.tags.length===0) return <p>there's no tag</p> ;
    //     else return <ol> {this.state.tags.map((tag)=> <li key={tag}>{tag}</li>) }</ol>;
    // }

    getdecreament=()=>{
        const disbled= <button disabled onClick={()=> {this.props.onDecrement(this.props.counter)}}  className='btn btn-secondary btn-lg '>
                        -
                       </button>
        const enabled= <button  onClick={()=> {this.props.onDecrement(this.props.counter)}}  className='btn btn-secondary btn-lg '>
                        -
                        </button>
    return this.props.counter.value !==0 ? enabled :disbled

    }

    render(){


        return (

            <div className="container">

                <div className="row row-cols-auto ">

                <div className="col-sm- mr-3 w-15 h-15">
                    <span className={this.props.value!==0?'badge badge-primary m-2':'badge badge-warning m-2'}>
                    {this.props.value}
                    </span>
                </div>


                <div className="col-sm- m-2 w-15 h-15">
                         <button  onClick={()=> {this.props.onIncrement(this.props.counter)}}  className='btn btn-secondary btn-lg'>
                            +
                        </button>
                </div>

                <div 
                    className="col-sm- m-2 w-15 h-15">{this.getdecreament()}
                </div>

                <div className="col-sm- m-2 w-15 h-15">
                    <button className="btn btn-danger btn " onClick={()=>{this.props.onDelete(this.props.counter.id)}}>
                        X
                    </button>
                </div>

                </div>
            </div>        

                )
    }

}
export default Counter;

