import React,{Component} from 'react';

class Counter extends Component{
    state={
        count:0,
        styles:{
            fontSize:50,
            fontWeight:'bold'
        },
        tags:['tag1','tag2','tag3'],
    };

    getTag(){
        if (this.state.tags.length===0) return <p>there's no tag</p> ;
        else return <ol> {this.state.tags.map((tag)=> <li key={tag}>tag</li>) }</ol>;
    }
    change = ()=> {
       // this.setState( { count :this.state.count +1 }); 
        this.setState( { tags : ["2","3","4"] });
        console.log(this.state);

    }

    render(){
        return (<React.Fragment> 
                    <span style={this.state.styles}  className={this.state.count !==0?'badge badge-primary m-2':'badge badge-warning m-2'}>{getcount(this.state)}</span>
                    <button style={this.state.styles} onClick={this.change}  className='btn btn-secondary btn-lg'>increamnet</button>
                    <div>{this.getTag()}</div>
               </React.Fragment>);
    }

}
export default Counter;

function getcount(state){
    const {count}=state
    return count ===0? "Zero" : count ; 
}
