import React, { Component } from 'react';
import {getMovies} from '../services/fakeMovie.js' 
import Heart from './heart'

class Table extends Component {
    state = { 
        movie:getMovies(),
        C:"fas fa-heart"
     }

    handledlete(id){

        this.setState({movie: deleteMovie(id,this.state.movie)})

       console.log(this.state.movie)
    };  

    like=()=>{
        const C= this.state.classes === "fas fa-heart"? "far fa-heart":"fas fa-heart"
        this.setState({C})
    }
    getTable(){

        return this.state.movie.map((movie) =>(
                <tr key={movie.title}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>{movie.numberInStock}</td>
                    <td><Heart onClick={this.like} classes={this.state.C} /></td>
                    <td className="m-2">
                      <button onClick={(event)=>{this.handledlete(event.currentTarget.id);}} className='btn btn-warning btn ' id={movie._id}>delete</button>
                    </td>

                </tr>) );
            };
    render() {

        return (
            <React.Fragment>
                <nav class="navbar navbar-light bg-light">
                    <div class="container-fluid">
                    <span class="navbar-brand mb-0 h1">{this.state.movie.length }</span>
                    </div>
                </nav>

                <table id='table1'>
                        <tbody>
                        <tr>
                        <th>title</th>
                        <th>genre</th>
                        <th>dailyRentalRate</th>
                        <th>numberInStock</th>

                        </tr>


                        {this.getTable()}
                        </tbody>
                    </table>): (<p> no such data</p>)
            </React.Fragment>);
    }
}

export default Table;

function deleteMovie(id,movies){
    var i=0
    for(let movie of movies){
        if (movie._id===id){
            movies.splice(i,1)}
        i++
    }

    return movies
  }
