import React, { Component } from 'react';
import {getMovies} from '../services/fakeMovie.js' 

class Table extends Component {
    state = { 
        movie:getMovies()
     }
    handledlete(id){
        console.log(id)
      //  this.setState({movie: deleteMovie(id,this.state.movie)})
    };  

    getTable(){

        return this.state.movie.map((movie) =>(
                <tr key={movie.title}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>{movie.numberInStock}</td>
                    <td>
                      <button onClick={(event)=>{console.log(this.handledlete(event.currentTarget.id));}} className='btn btn-warning btn' id={movie._id}>delete</button>
                    </td>

                </tr>) );
            };
    render() {

        return (
                    <table id='table1'>
                        <tbody>
                        <tr>
                        <th>title</th>
                        <th>genre</th>
                        <th>dailyRentalRate</th>
                        <th>numberInStock</th>

                        </tr>


                        {this.getTable()}
                        </tbody>
                    </table>
                )
    }
}

export default Table;

function deleteMovie(id,movies) {
    let movieInDb = movies.find(m => m._id === id);
    movies.splice(movies.indexOf(movieInDb), 1);
    return movieInDb;
  }
