import React, { Component } from 'react';
import {getMovies} from '../services/fakeMovie.js' 

class Table extends Component {
    state = { 
        movie:getMovies()
     }
    handledlete(id){

        this.setState({movie: deleteMovie(id,this.state.movie)})

       console.log(this.state.movie)
    };  

    getTable(){

        return this.state.movie.map((movie) =>(
                <tr key={movie.title}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>{movie.numberInStock}</td>
                    <td>
                    <i class="fas fa-heart"></i>
                      <button onClick={(event)=>{this.handledlete(event.currentTarget.id);}} className='btn btn-warning btn' id={movie._id}>delete</button>
                    </td>

                </tr>) );
            };
    render() {

        return (
                  this.state.movie.length !==0 ? (<table id='table1'>
                        <tbody>
                        <tr>
                        <th>title</th>
                        <th>genre</th>
                        <th>dailyRentalRate</th>
                        <th>numberInStock</th>

                        </tr>


                        {this.getTable()}
                        </tbody>
                    </table>): (<p> no such data</p>)
                );
    }
}

export default Table;

function deleteMovie(id,movies){
    var i=0
    for(let movie of movies){
        if (movie._id===id){
            movies.splice(i,1)}
        i++
    }

    return movies
  }
